import './App.css';
import Form from './components/Form';
import Nav from './components/Nav'; 
import Cards from './components/Cards.jsx';
import { useState } from 'react';
import axios from 'axios';
import {Routes, Route, useLocation} from 'react-router-dom';
import About from "./components/About";
import Detail from "./components/Detail";

const URL_Base= "https://be-a-rym.up.railway.app/api/character";


const API_KEY="85db08eae64c.68e9903bb2065a4f0bc8";

function App() {
   // const example = {
   //    id: 1,
   //    name: 'Rick Sanchez',
   //    status: 'Alive',
   //    species: 'Human',
   //    gender: 'Male',
   //    origin: {
   //       name: 'Earth (C-137)',
   //       url: 'https://rickandmortyapi.com/api/location/1',
   //    },
   //    image: 'https://rickandmortyapi.com/api/character/avatar/1.jpeg',
   // };
   // const onSearch = () => {   // mi handleClick, el evento q modifica el estado
   //    setCharacters([...characters, example])  //guarda el estado y le concatena example
   // }
   const [characters, setCharacters] = useState([])
   const location = useLocation()
   console.log(location)

   const onSearch= (id) => { 
      axios(`${URL_Base}/${id}?key=${API_KEY}`)
      .then(response => response.data)
      .then(( data ) => {        // axios me da un objeto gigante, hacemos destructuring y la response esta en data. o hacemos un then mas y no lo ponemos entre llaves
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            alert('¡No hay personajes con este ID!');
         }
      });
   }

   const onClose = (id) => {
     
      const charsFiltered = characters.filter(character => Number(character.id) !== Number(id))
      console.log(charsFiltered)
      setCharacters(charsFiltered)

   }

   return (
      <div className='App'>
         <Nav onSearch={onSearch}/>
         <Routes>

         <Route path="/home" element={<Cards onClose={onClose} characters={characters}/>}/>
         <Route path="/about" element={<About/>}/> 
         <Route path="/detail/:detailId" element={<Detail/>}/>
         </Routes>

      </div>
   );
}

export default App;
