import { ADD_FAVORITE, DELETE_FAVORITE } from "./actions-types"


const initialState = {
    myFavorites: []
}
const reducer = (state= initialState, action)=>{
    switch(action.type){
        case ADD_FAVORITE:
            return{
                type:[...state.myFavorites, action.payload]
            }
        case DELETE_FAVORITE:
            return{
                ...state,
                myFavorites: state.myFavorites.filter(char => Number(char.id) !== Number(action.payload.id))
            }
        default:
            return{...state}
    }
}
export default reducer;
