import React from "react";
import { Link } from "react-router-dom";
import { ADD_FAV, REMOVE_FAV } from "../redux/actions-types";
import { connect } from "react-redux";
import { useEffect, useState } from "react";
import "./stylesheet/tarjeta.css"
// const Card = (props) => {    // y con las propiedades con el pros.  por ej props.name  props.status

export const Card = ({id,name,status,species,gender,origin,image,onClose}) => {   
   
   const [isFav, setIsFav] = useState(false);
   
   function handleFavorite(){
   if(isFav){
      setIsFav(false)
      //le pasamos el id del personaje
      REMOVE_FAV(id)
   }
   else {
      setIsFav(true)
      ADD_FAV(id)
      
   }
   }
   

   
   
   return (
      <div className="tarjetas">
         <button className="eliminar" onClick={() => onClose(id)}>X</button>
       <div className="link"><Link to={`/detail/${id}`}><h2>{name}</h2></Link>
       </div>  
       <img className="imagen" src={image} alt='Imagen del personaje' />
        <div className="datos"> <h2>{status}</h2>
         <h2>{species}</h2>
         <h2>{gender}</h2>
         <h2>{origin.name}</h2>
         </div>
         <div>
            {     
          isFav?(
            <button onClick={handleFavorite}>red</button>
         ) : ( 
            <button onClick={handleFavorite}>black</button>
         )
      }
         </div>
      </div>
 
   );
}



   export function mapStateToProps(state){
      return{
         myFavorites: state.myFavorites
      }
   }
   export function mapDispatchToProps(dispatch){
      return{
        ADD_FAV: function(character){
          dispatch.ADD_FAV(character)
         },
         REMOVE_FAV: function(id){
            dispatch.REMOVE_FAV(id)
         }
      }
   }
   export default connect(
      mapStateToProps, mapDispatchToProps
   )(Card)
